import './form.scss';

const handleSubmit = (e:any, callback: Function) => {
  console.log("e on handle submit --->", e);
  e.preventDefault();
  console.log('handle sub e.target', e)
  const formData = {
    method:'GET',
    url: e.target[0].value//'https://pokeapi.co/api/v2/pokemon',
  };
  callback(formData);
}

export const Form = (props:any) => {
  return (
    <>
      <form onSubmit={(e) =>handleSubmit(e, props.APICall)}>
        <label >
          <span>Enter URL: </span>
          <input name='url' type='text' />
          <button type="submit">GO!</button>
        </label>
        <select className="methods">
          <option>GET</option>
          <option>POST</option>
          <option id="put">PUT</option>
          <option id="delete">DELETE</option>
        </select>
      </form>
    </>
  );
}
