import './form.scss';
import {Body} from "../body/body"
import { useState, useReducer, useCallback } from 'react';

// everything with these properties will be treated as the type "FormState"
// interface is used to make types that you specify. 
// follows principals of "duck typing"
interface FormState {
  method: "GET" | "POST" | "PUT" | "DELETE";
  url: string;
  body: object | undefined;
  response: object | undefined;
}

//Change state.body to undefined if the a method is selected which does not require the body
export const changeMethod = (
  state: FormState, 
  method: "GET" | "POST" | "PUT" | "DELETE",
) => {
  if (method === "GET" || method === "PUT"){
    state.body = undefined
  } return{
    ...state, method
  };
};

// Change state.body to an object if the a method is selected which does not require the body
export const setBody = (
  state: FormState,
  body: object
) => {
  if(state.method !== "GET" && state.method !== "PUT") {
    state.body = body;
  };
  return state;
}






export const Form = (props:any) => {
  // const [url, setUrl] = useState('');
  const [method, setMethod] = useState(null);

const handleSubmit = (e:any, callback: Function) => {
  e.preventDefault();
  let formData = {
    method: method,
    url: e.target[0].value,
  };
  callback(formData);
}

const handleMethodChange = (e:any) => {
  e.preventDefault();
  setMethod(e.target.value);
}





  return (
    <>
      <form onSubmit={(e) =>handleSubmit(e, props.APICall)}>
        <label >
          <span>Enter URL: </span>
          <input name='url' type='text' />
          <button type="submit">GO!</button>
        </label>
        <div className="methods">
          <button onClick = {(e) => handleMethodChange(e)} value = "GET" >GET</button>
          <button onClick = {(e) => handleMethodChange(e)} value = "POST">POST</button>
          <button onClick = {(e) => handleMethodChange(e)} value = "PUT">PUT</button>
          <button onClick = {(e) => handleMethodChange(e)} value = "DELETE">DELETE</button>
        </div>
      </form>
        <Body/>
    </>
  );
}
