import React, { useState, useReducer, useCallback } from 'react';

import './app.scss';

import { Header } from './components/header/header';
import { Footer } from './components/footer/footer';
import { Form } from './components/form/form';
import { Results } from './components/results/results';
import { FormState } from './components/form/form';

export interface AppState {
  data: object | undefined,
  requestParams:{
    url: string,
    method: string,
  },
  history: any[]
}

export const dispatchAction = (
  state: AppState,
  action: {
    action: string,
    body: any,
  }) => {
  switch (action.action) {
    case 'data':
      return setData(state, action.body as object)
    case 'requestParams':
      return setRequestParams(state, action.body as object)
    case 'history':
      return setHistory(state, action.body as any[])
    default:
      return state;
  }
}

export const setData = (
  state: AppState,
  data: object,
) => {
  state.data = data;
  return {...state};
}

export const setRequestParams = (
  state: AppState,
  requestParams:{
    url: string,
    method: string,
  },
) => {
  state.requestParams = requestParams;
  console.log("state req par in setReqPar---->", state.requestParams);
  return state;
}

export const setHistory = (
  state: AppState,
  data: object,
) => {
  state.history.push(data);
  console.log("state on set history after state.history assigned value----->", state);
  return state;
}




const App: React.FC = () => {
  const [state, dispatch] = useReducer(
    dispatchAction, {
    data: {},
    requestParams: {
      method: "Pick a method",
      url: "Pick an API"
    },
    history: [],
  }
  )
 console.log("Initial app state", state)

  async function APICall(req: any) {
    console.log("req on APP API call", req);

    if (req.method === "GET") {
      console.log("req on API call --->", req);
      const res = await fetch(req.url, { method: req.method });
      let parsedRes = await res.json();

      // setData(state,{ parsedRes });
      dispatch({action: "data", body:parsedRes })
      dispatch({action: "requestParams", body:req })
      dispatch({action: "history", body:parsedRes })
      // setRequestParams(state,req);
      // setHistory(state,{ parsedRes });

    } else {
      state.requestParams = req;
      state.history.push(state.data);
      state.data = {};
    }
  }

  return (
   <>
   {/* {console.log(" APP state before passing down", {state})} */}
    <Header />
      <section>
        <div>Request Method: {state.requestParams.method}</div>
        <div>URL: {state.requestParams.url}</div>
      </section>
      <div id="main">
        <Form APICall={APICall} />
        <Results data={state} />
      </div>
      <Footer/>
   </>

  );
}

export default App;
