import React from 'react';

import './form.scss';




const handleSubmit = (e:React.FormEvent<HTMLFormElement>, callback: Function) => {
  e.preventDefault();
  console.log('event.target ---> ', e.target.input)
  const formData = {
    method:'GET',
    url: e.target//'https://pokeapi.co/api/v2/pokemon',
  };
  callback(formData);
}

export const Form = (props:any) => {

  return (
    <>
      <form onSubmit={(event) =>handleSubmit(event, props.APIcall)}>
        <label >
          <span>Enter URL: </span>
          <input name='url' type='text' defaultValue='https://pokeapi.co/api/v2/pokemon' />
          <button type="submit">GO!</button>
        </label>
        <label className="methods">
          <span id="get">GET</span>
          <span id="post">POST</span>
          <span id="put">PUT</span>
          <span id="delete">DELETE</span>
        </label>
      </form>
    </>
  );
}
