import React, {useState} from 'react';

import './app.scss';

import {Header} from './components/header/header';
import {Footer} from './components/footer/footer';
import {Form} from './components/form/form';
import {Results} from './components/results/results';



const App = () => {
 const [data, setData] = useState({});
 const [requestParams, setRequestParams] = useState({method: "Pick a method", url: "Pick an API"});
 const [history, setHistory] = useState({})
 let holderArr: any[] = [];
 async function APICall (req:any){
  const res = await fetch(req.url, {method: req.method});
  let parsedRes = await res.json();
  let data = {parsedRes};
  console.log("data on app, --->", data);
  holderArr.push(data);
  setData(data);
  setRequestParams(req);
  setHistory(holderArr);
  console.log(holderArr);
  console.log(history);
 }

    return (
      <>
        <Header />
        <section>
        <div>Request Method: {requestParams.method}</div>
        <div>URL: {requestParams.url}</div>
        </section>
        <div id="main">
          <Form APICall={APICall} />
        <Results data={data} />
        </div>

        <Footer />
      </>
    );
}




export default App;
