import React, { useState, useReducer } from 'react';

import './app.scss';

import { Header } from './components/header/header';
import { Footer } from './components/footer/footer';
import { Form } from './components/form/form';
import { Results } from './components/results/results';
import { FormState } from './components/form/form';

export interface AppState {
  data: object | undefined,
  requestParams: object | undefined,
  history: any[]
}

export const dispatchAction = (
  state: AppState,
  action: {
    action: string,
    body: unknown,
  }) => {
  switch (action.action) {
    case 'data':
      return setData(state, action.body as object)
    case 'requestParams':
      return setRequestParams(state, action.body as object)
    case 'history':
      return setHistory(state, action.body as any[])
    default:
      return state;
  }
}

export const setData = (
  state: AppState,
  data: object,
  ) => {
   state.data = data;
   console.log("state on set data after state.data assigned value----->", state);
   return state;
}

export const setRequestParams = (
  state: AppState,
  requestParams: object,
  ) => {
   state.requestParams = requestParams;
   console.log("state on set requestParams after state.requestParams assigned value----->", state);
   return state;
}

export const setHistory = (
  state: AppState,
  history: any[],
  ) => {
   state.history = history;
   console.log("state on set history after state.history assigned value----->", state);
   return state;
}




const App = () => {
  const [state, dispatch] = useReducer(
    dispatchAction, {
    data: {},
    requestParams: {
      method: "Pick a method",
      url: "Pick an API"
    },
    history: [],
  }
  )

  const updateData = (data: object) => {
    dispatch({action: "data", body: data})
  }




  const [data, setData] = useState({});
  const [requestParams, setRequestParams] = useState({ method: "Pick a method", url: "Pick an API" });



  async function APICall(req: any) {
    // setHistory()
    console.log("req on APP API call", req);
    if (req.method === "GET") {
      const res = await fetch(req.url, { method: req.method });
      let parsedRes = await res.json();
      let data = { parsedRes };
      updateData(data);
      setRequestParams(req);
    } else {
      setRequestParams(req);
      setData({});
    }
  }

  return (
    <>
      <Header />
      <section>
        <div>Request Params:</div>
        <div>Request Method: {requestParams.method}</div>
        <div>URL: {requestParams.url}</div>
      </section>
      <div id="main">
        <Form response={data} APICall={APICall} />
        <Results data={data} />
      </div>
      <Footer />
    </>
  );
}




export default App;
export { };
